		this.disableButton = this.disableButton.bind(this)
	}
	componentDidMount(){
    	var constraints = { audio: true, video: { width: 500, height: 500}, facingMode: { exact: "user" } };
    	video = document.querySelector("#video");
	    canvas = document.querySelector("#canvas");
		video.width = constraints["video"]["width"];
  		video.height = constraints["video"]["height"];
  		canvas.width = constraints["video"]["width"];
	    canvas.height = constraints["video"]["height"];
	    canvasCtx = canvas.getContext('2d');
	    canvasCtx.translate(canvas.width, 0);
		canvasCtx.scale(-1, 1);
  		//var loadModel = this.faceDetect;
    	navigator.mediaDevices.getUserMedia(constraints)
    	.then((stream) => {
	        video.srcObject = stream;
	        video.onloadedmetadata = () => {
	        	video.play();
	        };
     	})
     	.catch(function(err) {
        	console.log(err)
        }); 
	}
	async disableButton() {
		if(this.state.wait == false){
			await this.setState({wait: true}, () => this.sendPhoto())
		}
	}
	async sendPhoto(){
		var hidden_canvas = document.createElement('canvas');
		hidden_canvas.width = canvas.width;
		hidden_canvas.height = canvas.height;
		var newCtx = hidden_canvas.getContext('2d');
		newCtx.drawImage(video,0,0,500,500)
		//newImage.src = canvas.toDataURL()
		hidden_canvas.toBlob((blob) => {
		const formData = new FormData();
			formData.append("image", blob);
			//console.log(blob)
			axios({
				method: 'post',
    			url: 'http://127.0.0.1:8000/',
    			data: formData,
    			headers: {'Content-Type': 'multipart/form-data' }
			})
			.then(res => {
				let top, left, size;
				if(res.data.label === "no face"){
					top = 100;
					left = 100;
					size = 200;
				}
				else{
					top = res.data.top;
					left = res.data.left;
					size = res.data.bottom-top;
				}
				canvasCtx.drawImage(video,0,0,500,500)
				canvasCtx.save();
				canvasCtx.beginPath();
				canvasCtx.rect(500-size-left,top, size, size)
				canvasCtx.strokeStyle = 'blue';
				canvasCtx.stroke(); 
				canvasCtx.translate(canvas.width, 0);
				canvasCtx.scale(-1, 1);
				canvasCtx.fillStyle = "white";
				canvasCtx.font = "20pt Arial";
				canvasCtx.fillText(res.data.label, left,top)
				canvasCtx.restore();
				console.log(res)
				this.setState({wait: false})
			})
			.catch(err =>{
				//console.log(err)
				})
			})
	}

