import React from 'react';
import {Container, Row, Col, Jumbotron} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as blazeface from '@tensorflow-models/blazeface';
import '@tensorflow/tfjs-backend-webgl';
import './styles.css';
class Camera extends React.Component{
	constructor(props){
		super(props);
		this.state ={
			video: null,
		}
		this.loadFaceDetect = this.loadFaceDetect.bind(this)
		this.drawBox = this.drawBox.bind(this)
	}
	async componentDidMount(){

    	var constraints = { audio: true, video: { width: 720, height: 500 } };
    	var loadModel = this.loadFaceDetect;
    	navigator.mediaDevices.getUserMedia(constraints)

    	.then((stream) => {
	        var video = document.querySelector("video");
	        video.srcObject = stream;
	        video.onloadedmetadata = () => {
	        	video.play();
	        };
	        video.onplay = () => loadModel()
     	})
     	.catch(function(err) {
        	console.log("no video")
        	console.log(err)
        }); 
	}

	async loadFaceDetect() {
		const returnTensors = false;
		const model = await blazeface.load();
     	const video = document.querySelector("video");
		const predictions = await model.estimateFaces(video, returnTensors);
		if (predictions.length > 0) {
    		this.drawBox(predictions)
    	}
	}

	drawBox(predictions) {
		var video = document.querySelector("#video");
    	var canvas = document.querySelector("#videoCanvas");
		var ctx = canvas.getContext('2d');


    	canvas.width = video.videoWidth;
    	canvas.height = video.videoHeight;
    	ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    	for (let i = 0; i < predictions.length; i++) {
	      const start = predictions[i].topLeft;
	      const end = predictions[i].bottomRight;
	      const size = [end[0] - start[0], end[1] - start[1]];

	      // Render a rectangle over each detected face.
	      ctx.fillRect(start[0], start[1], size[0], size[1]);
    	}
    	requestAnimationFrame(drawBox);
	}


	render(){
		return(
			<Container fluid>
					<Jumbotron>
						<Row className="justify-content-md-center">
					  		<h1 className="title">smileAI</h1>
					  	</Row>
					  	<Row className="justify-content-md-center">
						  <p>
						    Simple AI that detects smiles. Positive vibes only.
						  </p>
						</Row>
						<Row className="justify-content-md-center">
							<p>
								<small>Neural network built without any deep learning libraries (only numpy).</small>
							</p>
						</Row>
					</Jumbotron>
				<Row className="justify-content-md-center">
				    <Col xs={6}>
				    	<canvas id="videoCanvas"></canvas>
				    	<video id="video" autoPlay={true} src={this.state.video}></video>
				    </Col>
				</Row>
			</Container>

		)
	}
}

export default Camera;
