import React from 'react';
import {Container, Row, Col, Jumbotron} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as blazeface from '@tensorflow-models/blazeface';
import '@tensorflow/tfjs-backend-webgl';
import './styles.css';
let model, canvas,canvasCtx, video, newImage;
class Camera extends React.Component{
	constructor(props){
		super(props);
		this.state ={
			video: null,
		}
		this.faceDetect = this.faceDetect.bind(this)
		this.drawBox = this.drawBox.bind(this)
	}
	async componentDidMount(){
		model = await blazeface.load();
    	var constraints = { audio: true, video: { width: 720, height: 500}, facingMode: { exact: "user" } };
    	video = document.querySelector("#video");
	    canvas = document.querySelector("#videoCanvas");
		video.width = constraints["video"]["width"];
  		video.height = constraints["video"]["height"];
  		canvas.width = constraints["video"]["width"];
	    canvas.height = constraints["video"]["height"];
	    canvasCtx = canvas.getContext('2d');
	    canvasCtx.translate(canvas.width, 0);
		canvasCtx.scale(-1, 1);
  		var loadModel = this.faceDetect;
    	navigator.mediaDevices.getUserMedia(constraints)
    	.then((stream) => {
	        //var video = document.querySelector("#video");
	        video.srcObject = stream;
	        video.onloadedmetadata = () => {
	        	video.play();
	        };
	        video.onplay = () => loadModel()
     	})
     	.catch(function(err) {
        	console.log("no video")
        	console.log(err)
        }); 
        newImage = document.createElement('img');
        document.body.append(newImage);


	}

	async faceDetect() {
		const returnTensors = false;
		const predictions = await model.estimateFaces(video, returnTensors);
    	this.drawBox(predictions)
    	requestAnimationFrame(this.faceDetect)
	}

	drawBox(predictions) {
		if(predictions.length > 0) {
			canvasCtx.drawImage(video, 0,0)
			//canvasCtx.drawImage(video, 0, 0, canvas.width, canvas.height);
			for (let i = 0; i < predictions.length; i++) {
				const start = predictions[i].topLeft;
				const end = predictions[i].bottomRight;
				const size = [end[0] - start[0], end[1] - start[1]];
				// Render a bounding box over faces
				canvasCtx.beginPath();
				canvasCtx.rect(start[0], start[1], size[0], size[1]);
				console.log(predictions)
				console.log("trash")
				canvasCtx.strokeStyle = 'black';
				canvasCtx.stroke(); 
				var hidden_canvas = document.createElement('canvas');
				hidden_canvas.width = size[0]
				hidden_canvas.height = size[1]
				var newContent = hidden_canvas.getContext('2d');
				newContent.translate(hidden_canvas.width, 0);
				newContent.scale(-1, 1);
				newContent.drawImage(canvas, start[0], start[1], size[0], size[1], start[0], start[1], size[0],size[1])
				//var newImage = document.createElement('img');
				newImage.src = hidden_canvas.toDataURL();
	    	}
	    }
	}


	render(){
		return(
			<Container fluid>
					<Jumbotron>
						<Row className="justify-content-md-center">
					  		<h1 className="title">smileAI</h1>
					  	</Row>
					  	<Row className="justify-content-md-center">
						  <p>
						    Simple AI that detects smiles. Positive vibes only.
						  </p>
						</Row>
						<Row className="justify-content-md-center">
							<p>
								<small>Neural network built without any deep learning libraries (only numpy).</small>
							</p>
						</Row>
					</Jumbotron>
				<Row className="justify-content-md-center">
				    <Col xs={6}>
				    	<canvas id="videoCanvas"></canvas>
				    	<video id="video" autoPlay={true} playsInline={true} src={this.state.video} className="video"></video>
				    </Col>
				</Row>
			</Container>

		)
	}
}

export default Camera;
